import React, { useState, useEffect } from 'react';
import { AppBar, Box, Container, Typography, Table, TableBody,  TableContainer, TableHead, TableRow, Paper, Button, Snackbar } from '@mui/material';
import { Alert } from '@mui/material';

export default function Cart({ cart, setCart }) {
    
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');

  const handleRemoveFromCart = (stock) => {
    setCart(cart.filter((item) => item.ticker !== stock.ticker));
    setOpenSnackbar(true);
    setSnackbarMessage(`${stock.ticker} removed from cart.`); // Updated line
    console.log(StyledTableCell); // Should log the styled component

  };

  const handleSnackbarClose = () => {
    setOpenSnackbar(false);
  };

  useEffect(() => {
    // Optional: Simulate a delay for the snackbar message (2 seconds)
    const timeoutId = setTimeout(() => {
      setOpenSnackbar(false);
    }, 2000);

    return () => clearTimeout(timeoutId);
  }, [openSnackbar]);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', backgroundColor: 'background.default' }}>
      <AppBar position="static" color="primary">
        <Typography variant="h6" component="h1" sx={{ color: 'inherit', fontWeight: 'bold' }}>
          Your Cart
        </Typography>
      </AppBar>
      <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
        {cart.length > 0 ? (
          <TableContainer component={Paper} sx={{ mt: 4, backgroundColor: 'background.paper' }}>
            <Table sx={{ '& thead th': { backgroundColor: 'background.default', color: 'text.primary' } }}>
              <TableHead>
                <TableRow>
                  <StyledTableCell>Ticker</StyledTableCell>
                  <StyledTableCell>Price</StyledTableCell>
                  <StyledTableCell>Change (%)</StyledTableCell>
                  <StyledTableCell>Action</StyledTableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {cart.map((stock, index) => (
                  <TableRow key={index}>
                    <StyledTableCell component="th" scope="row">
                      {stock.ticker}
                    </StyledTableCell>
                    <StyledTableCell>{stock.price}</StyledTableCell>
                    <StyledTableCell>{stock.change_percentage}</StyledTableCell>
                    <StyledTableCell>
                      <Button variant="contained" color="error" size="small" onClick={() => handleRemoveFromCart(stock)}>
                        Remove
                      </Button>
                    </StyledTableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        ) : (
          <Typography variant="h6" component="h2" sx={{ mt: 4, textAlign: 'center' }}>
            Your cart is currently empty.
          </Typography>
        )}
      </Container>
      <Snackbar
        open={openSnackbar}
        autoHideDuration={2000} // Optional: Set duration automatically (2 seconds)
        onClose={handleSnackbarClose}
      >
        <Alert onClose={handleSnackbarClose} severity="success" sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
}
